    expected = window × (numbers_per_draw / pool_size)

    hot = []
    cold = []

    for number in 1 to pool_size:
        ratio = frequencies[number] / expected

        if ratio > 1.2:
            hot.append(number)
        else if ratio < 0.8:
            cold.append(number)

